#[derive(Deserialize, Debug)]
#[serde(rename_all = "camelCase")]
#[serde(untagged)]
pub enum LnurlRequestData {
    PayRequest {
        #[serde(flatten)]
        data: LnurlPayRequest,
    },
    WithdrawRequest {
        #[serde(flatten)]
        data: LnurlWithdrawRequestData,
    },
    #[serde(rename = "login")]
    AuthRequest {
        #[serde(flatten)]
        data: LnurlAuthRequestData,
    },
    Error {
        #[serde(flatten)]
        data: LnurlErrorData,
    },
}

